const HTTP_METHOD={POST:'POST'};function isPOST(a){return a===HTTP_METHOD.POST}function isJSON(a){return-1<a.indexOf('application/json')}module.exports=(a,b,c)=>{const d=a.method,e=a.headers['content-type'];if(isPOST(d)&&isJSON(e)){let f='';a.on('data',g=>{f+=g}),a.on('end',()=>{const g=JSON.parse(f);a.body=g,c()})}else c()};
